/*Table structure for table `hml_article` */

CREATE TABLE `hml_article` (
  `article_id` int unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int NOT NULL DEFAULT '0' COMMENT '店铺id',
  `site_name` varchar(255) NOT NULL DEFAULT '' COMMENT '店铺名称',
  `article_title` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `article_abstract` varchar(255) NOT NULL DEFAULT '' COMMENT '摘要',
  `category_id` int NOT NULL DEFAULT '0' COMMENT '分类id',
  `cover_img` varchar(2000) NOT NULL DEFAULT '' COMMENT '封面图片',
  `article_content` text COMMENT '内容',
  `status` tinyint NOT NULL DEFAULT '0' COMMENT '状态（0草稿箱  1发布）',
  `is_show_release_time` tinyint NOT NULL DEFAULT '0' COMMENT '发布时间是否显示',
  `is_show_read_num` tinyint NOT NULL DEFAULT '0' COMMENT '阅读数是否显示',
  `is_show_dianzan_num` tinyint NOT NULL DEFAULT '0' COMMENT '点赞数是否显示',
  `read_num` int NOT NULL DEFAULT '0' COMMENT '阅读数',
  `dianzan_num` int NOT NULL DEFAULT '0' COMMENT '点赞数',
  `create_time` int DEFAULT '0',
  `update_time` int NOT NULL DEFAULT '0',
  `initial_read_num` int NOT NULL DEFAULT '0' COMMENT '初始阅读数',
  `initial_dianzan_num` int NOT NULL DEFAULT '0' COMMENT '初始点赞数',
  `sort` int NOT NULL DEFAULT '0' COMMENT '排序',
  PRIMARY KEY (`article_id`),
  KEY `IDX_ns_article_class_id` (`category_id`),
  KEY `IDX_ns_article_site_id` (`site_id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8mb3 COMMENT='文章表';

