  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '标识',
  `title` varchar(50) NOT NULL DEFAULT '' COMMENT '组件名称',
  `type` varchar(50) NOT NULL DEFAULT 'SYSTEM' COMMENT '组件类型',
  `value` text COMMENT '配置:json格式',
  `addon_name` varchar(50) NOT NULL DEFAULT '' COMMENT '插件标识',
  `sort` int NOT NULL DEFAULT '0' COMMENT '排序号',
  `support_diy_view` varchar(500) NOT NULL DEFAULT '' COMMENT '支持的自定义页面（为空表示公共组件都支持）',
  `max_count` int NOT NULL DEFAULT '0' COMMENT '限制添加次数',
  `is_delete` int NOT NULL DEFAULT '0' COMMENT '是否可以删除，0 允许，1 禁用',
  `icon` varchar(255) NOT NULL DEFAULT '' COMMENT '组件图标',
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`),
  KEY `IDX_nc_diy_view_util_sort` (`sort`),
  KEY `IDX_nc_diy_view_util_type` (`type`)
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=utf8mb3 COMMENT='自定义模板组件';

