/*Table structure for table `hml_goods_virtual` */

CREATE TABLE `hml_goods_virtual` (
  `id` int NOT NULL AUTO_INCREMENT,
  `site_id` int NOT NULL DEFAULT '0' COMMENT '店铺id',
  `order_id` int NOT NULL DEFAULT '0' COMMENT '订单id',
  `order_no` varchar(255) NOT NULL DEFAULT '' COMMENT '订单编号',
  `sku_id` int NOT NULL DEFAULT '0' COMMENT '商品sku_id',
  `sku_name` varchar(255) NOT NULL DEFAULT '' COMMENT '商品名称',
  `code` varchar(255) NOT NULL DEFAULT '' COMMENT '虚拟商品编码',
  `is_veirfy` int NOT NULL DEFAULT '0' COMMENT '是否已经核销',
  `verify_time` int NOT NULL DEFAULT '0' COMMENT '核销时间',
  `member_id` int NOT NULL DEFAULT '0' COMMENT '所属人',
  `sku_image` varchar(255) NOT NULL DEFAULT '' COMMENT '虚拟商品图片',
  `expire_time` int NOT NULL DEFAULT '0' COMMENT '核销有效期 0永久 ',
  `card_info` varchar(2500) NOT NULL DEFAULT '' COMMENT '卡密信息',
  `sold_time` int NOT NULL DEFAULT '0' COMMENT '售出时间',
  `goods_id` int NOT NULL DEFAULT '0' COMMENT '商品id',
  `verify_total_count` int NOT NULL DEFAULT '0' COMMENT '核销次数',
  `verify_use_num` int NOT NULL DEFAULT '0' COMMENT '已核销次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COMMENT='用户虚拟商品表';

