  `app_module` varchar(255) NOT NULL DEFAULT '' COMMENT '使用端口',
  `group_name` varchar(50) NOT NULL DEFAULT '' COMMENT '用户组名称',
  `group_status` int NOT NULL DEFAULT '1' COMMENT '用户组状态',
  `is_system` int NOT NULL DEFAULT '0' COMMENT '是否是系统用户组',
  `menu_array` text COMMENT '系统菜单权限组，用，隔开',
  `desc` varchar(255) NOT NULL DEFAULT '' COMMENT '描述',
  `create_time` int NOT NULL DEFAULT '0' COMMENT '创建时间',
  `modify_time` int NOT NULL DEFAULT '0' COMMENT '修改时间',
  `create_user_data` varchar(500) NOT NULL DEFAULT '[]' COMMENT '创建用户数据 json格式',
