CREATE TABLE `hml_local` (
  `id` int NOT NULL AUTO_INCREMENT,
  `site_id` int NOT NULL DEFAULT '0' COMMENT '站点id',
  `type` varchar(255) NOT NULL DEFAULT 'default' COMMENT '配送方式  default 商家自配送  other 第三方配送',
  `area_type` int NOT NULL DEFAULT '1' COMMENT '配送区域',
  `local_area_json` text COMMENT '区域配送设置',
  `time_is_open` int NOT NULL DEFAULT '0' COMMENT '订单达是否开启 0 关闭 1 开启',
  `time_type` int NOT NULL DEFAULT '0' COMMENT '时间选取类型 0 全天  1 自定义',
  `time_week` varchar(255) NOT NULL DEFAULT '' COMMENT '营业时间  周一 周二.......',
  `start_time` int NOT NULL DEFAULT '0' COMMENT '当日的起始时间',
  `end_time` int NOT NULL DEFAULT '0' COMMENT '当日的营业结束时间',
  `update_time` int NOT NULL DEFAULT '0',
  `is_open_step` int NOT NULL DEFAULT '0' COMMENT '是否启用阶梯价(适用于行政区域)',
  `start_distance` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '多少距离以内,...',
  `start_delivery_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '多少距离以内,多少钱',
  `continued_distance` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '每增加多少距离',
