/*Table structure for table `hml_order_goods` */

CREATE TABLE `hml_order_goods` (
  `order_goods_id` int NOT NULL AUTO_INCREMENT,
  `order_id` int NOT NULL DEFAULT '0' COMMENT '订单id',
  `order_no` varchar(20) NOT NULL DEFAULT '' COMMENT '订单编号',
  `site_id` int NOT NULL DEFAULT '0' COMMENT '商家id',
  `member_id` int NOT NULL DEFAULT '0' COMMENT '购买会员id',
  `goods_id` int NOT NULL DEFAULT '0' COMMENT '商品id',
  `sku_id` int NOT NULL DEFAULT '0' COMMENT '商品skuid',
  `sku_name` varchar(255) NOT NULL DEFAULT '' COMMENT '商品名称',
  `sku_image` varchar(2000) NOT NULL DEFAULT '' COMMENT '商品图片',
  `sku_no` varchar(255) NOT NULL DEFAULT '' COMMENT '商品编码',
  `is_virtual` int NOT NULL DEFAULT '0' COMMENT '是否是虚拟商品',
  `goods_class` varchar(50) NOT NULL DEFAULT '0' COMMENT '商品种类(1.实物 2.虚拟3.卡券)',
  `goods_class_name` varchar(50) NOT NULL DEFAULT '' COMMENT '商品类型名称',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品卖价',
  `cost_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '成本价',
  `num` decimal(12,3) NOT NULL DEFAULT '0.000' COMMENT '购买数量',
  `goods_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品总价',
  `cost_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '成本总价',
  `delivery_status` int NOT NULL DEFAULT '0' COMMENT '配送状态',
  `delivery_status_name` varchar(50) NOT NULL DEFAULT '' COMMENT '配送状态名称',
  `delivery_no` varchar(50) NOT NULL DEFAULT '' COMMENT '配送单号',
  `gift_flag` int NOT NULL DEFAULT '0' COMMENT '赠品标识',
  `refund_no` varchar(50) NOT NULL DEFAULT '' COMMENT '退款编号（申请产生）',
  `refund_status` int NOT NULL DEFAULT '0' COMMENT '退款状态',
  `refund_status_name` varchar(50) NOT NULL DEFAULT '' COMMENT '退款状态名称',
  `refund_status_action` varchar(1000) NOT NULL DEFAULT '' COMMENT '退款操作',
  `refund_type` int NOT NULL DEFAULT '0' COMMENT '退款方式',
  `refund_apply_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '退款申请金额',
  `refund_reason` varchar(255) NOT NULL DEFAULT '' COMMENT '退款原因',
  `refund_real_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '实际退款金额',
  `refund_delivery_name` varchar(50) NOT NULL DEFAULT '' COMMENT '退款公司名称',
  `refund_delivery_no` varchar(20) NOT NULL DEFAULT '' COMMENT '退款单号',
  `refund_time` int NOT NULL DEFAULT '0' COMMENT '实际退款时间',
  `refund_refuse_reason` varchar(255) NOT NULL DEFAULT '' COMMENT '退款拒绝原因',
  `refund_action_time` int NOT NULL DEFAULT '0' COMMENT '退款时间',
  `real_goods_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '实际商品购买价',
  `refund_remark` varchar(255) NOT NULL DEFAULT '' COMMENT '退款说明',
  `refund_images` varchar(3000) NOT NULL DEFAULT '' COMMENT '退款图片 最多五张',
  `refund_delivery_remark` varchar(255) NOT NULL DEFAULT '' COMMENT '买家退货说明',
  `refund_address` varchar(255) NOT NULL DEFAULT '' COMMENT '退货地址',
  `is_refund_stock` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否返还库存',
  `refund_money_type` int NOT NULL DEFAULT '1' COMMENT '退款方式   1 原路退款 2线下退款3退款到余额',
  `shop_refund_remark` varchar(255) NOT NULL DEFAULT '' COMMENT '商家退款说明',
  `shop_active_refund` tinyint NOT NULL DEFAULT '0' COMMENT '商家主动退款（0否  1是）',
  `shop_active_refund_no` varchar(50) NOT NULL DEFAULT '' COMMENT '商家主动退款编号',
  `shop_active_refund_remark` varchar(255) NOT NULL DEFAULT '' COMMENT '商家主动退款说明',
  `shop_active_refund_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商家主动退款金额',
  `shop_active_refund_money_type` int NOT NULL DEFAULT '1' COMMENT '商家主动退款方式 1 原路退款 2线下退款3退款到余额',
  `refund_mode` int NOT NULL DEFAULT '1' COMMENT '退款类型 1退款 2售后',
  `promotion_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠金额',
  `coupon_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠券金额',
  `adjust_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '调整金额',
  `goods_name` varchar(400) NOT NULL DEFAULT '' COMMENT '商品名称',
  `sku_spec_format` varchar(1000) NOT NULL DEFAULT '' COMMENT 'sku规格格式',
  `is_fenxiao` int NOT NULL DEFAULT '1',
  `use_point` int NOT NULL DEFAULT '0' COMMENT '积分抵扣所用积分数',
  `point_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '积分抵扣金额',
  `refund_delivery_money` decimal(10,2) NOT NULL DEFAULT '0.00',
  `create_time` int NOT NULL DEFAULT '0',
  `out_aftersale_id` varchar(255) NOT NULL DEFAULT '' COMMENT '关联视频号订单',
  `refund_address_id` int NOT NULL DEFAULT '0' COMMENT '退货地址id',
  `refund_pay_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '真实退款金额',
  `store_id` int NOT NULL DEFAULT '0' COMMENT '所属门店',
  `card_item_id` int NOT NULL DEFAULT '0' COMMENT '卡项id',
  `card_promotion_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '次卡抵扣金额',
  `supplier_id` int NOT NULL DEFAULT '0' COMMENT '供应商id',
  `is_adjust_price` int NOT NULL DEFAULT '0' COMMENT '是否自定义价格',
  PRIMARY KEY (`order_goods_id`),
  KEY `IDX_ns_order_goods_goods_id` (`goods_id`),
  KEY `IDX_ns_order_goods_is_fenxiao` (`is_fenxiao`),
  KEY `IDX_ns_order_goods_is_virtual` (`is_virtual`),
  KEY `IDX_ns_order_goods_member_id` (`member_id`),
  KEY `IDX_ns_order_goods_order_id` (`order_id`),
  KEY `IDX_ns_order_goods_refund_status` (`refund_status`),
  KEY `IDX_ns_order_goods_sku_id` (`sku_id`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8mb3 COMMENT='订单商品表';

