/*Table structure for table `hml_site_diy_view1` */

CREATE TABLE `hml_site_diy_view1` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int NOT NULL DEFAULT '0' COMMENT '站点id',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '模板标识',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '模板名称',
  `template_id` int NOT NULL DEFAULT '0' COMMENT '所属模板id',
  `template_item_id` int NOT NULL DEFAULT '0' COMMENT '所属模板页面id，关联diy_template_goods_item表',
  `type` varchar(255) NOT NULL DEFAULT 'DIY_PAGE' COMMENT '页面类型',
  `type_name` varchar(255) NOT NULL DEFAULT '自定义页面' COMMENT '页面类型名称',
  `is_default` int NOT NULL DEFAULT '0' COMMENT '是否默认页面（针对自定义模板设置），1：是，0：否',
  `addon_name` varchar(255) NOT NULL DEFAULT '' COMMENT '插件标识',
  `click_num` int NOT NULL DEFAULT '0' COMMENT '浏览量',
  `value` longtext COMMENT '配置值',
  `sort` int NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` int NOT NULL DEFAULT '0' COMMENT '创建时间',
  `modify_time` int NOT NULL DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`id`),
  KEY `IDX_nc_site_diy_view` (`site_id`,`name`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb3 COMMENT='店铺自定义模板表';

