/*Table structure for table `ns_member_code_sequence` */

CREATE TABLE `ns_member_code_sequence` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键',
  `site_id` int NOT NULL DEFAULT '0' COMMENT '站点ID',
  `area_code` varchar(10) NOT NULL DEFAULT '' COMMENT '区号（去0后）',
  `member_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '会员类型 0普通 8特邀',
  `current_seq` int NOT NULL DEFAULT '0' COMMENT '当前自增序号',
  `create_time` int NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_key` (`site_id`,`area_code`,`member_type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='会员编号自增序列表';

